<?php	include("../_includes/session_test.inc");	include("../_includes/chabertdesign.inc");    include("../_includes/_fileUpload.inc"); // Include the file with the handleFileUpload function		unset($_GET);        $zt_name = isset($_POST['zt_name']) ? nl2br(stripslashes($_POST['zt_name'])) : retour_erreur(10, 7);    $element = $_POST['element'];        // Query the database to get the current file paths    $stmt = $db->prepare("SELECT client_logo, client_logo_white FROM clients WHERE id_client = ?");    $stmt->execute([$element]);    $currentFiles = $stmt->fetch();        // Define the valid file extensions    $extensions_valides = array('jpg', 'jpeg', 'png', 'svg');    $page = '10';    $error_code = '1025';        $destination = $currentFiles['client_logo']; // Initialize with the current client_logo    if ($_FILES['f_logo']['size'] > 0) {        // Delete existing client_logo file        if (!empty($currentFiles['client_logo'])) {            $fileToDelete = $_SERVER['DOCUMENT_ROOT'] . $currentFiles['client_logo'];            if (file_exists($fileToDelete)) {                unlink($fileToDelete);            }        }                // Handle file upload for new client_logo        $dossier = "/assets/img/clients/";        $destination = handleFileUpload($_FILES['f_logo'], $dossier, $extensions_valides, $page, $error_code);    }        $destination2 = $currentFiles['client_logo_white']; // Initialize with the current client_logo_white    if ($_FILES['f_logo_white']['size'] > 0) {        // Delete existing client_logo_white file        if (!empty($currentFiles['client_logo_white'])) {            $fileToDelete = $_SERVER['DOCUMENT_ROOT'] . $currentFiles['client_logo_white'];            if (file_exists($fileToDelete)) {                unlink($fileToDelete);            }        }                // Handle file upload for new client_logo_white        $dossier = "/assets/img/clients/";        $destination2 = handleFileUpload($_FILES['f_logo_white'], $dossier, $extensions_valides, $page, $error_code);    }    // Update the database with new file paths    $stmt2 = $db->prepare('UPDATE clients SET client_name = ?, client_logo = ?, client_logo_white = ? WHERE id_client = ?');    $execute2 = $stmt2->execute([$zt_name, $destination, $destination2, $element]);        if ($execute2) {        $stmt2->closeCursor();        retour_erreur(10,3);    } else {        retour_erreur(10,4);    }?>