<?php    include("../_includes/session_test.inc");    include("../_includes/chabertdesign.inc");        unset($_GET);        $element = $_POST['element'];        // Get the file URL from the database for the given element    $stmt = $db->prepare("SELECT url FROM images WHERE id_image = ?");    $stmt->execute([$element]);    $result = $stmt->fetch(PDO::FETCH_ASSOC);        // Check if a file URL was found    if ($result) {        $fileRelativePath = urldecode($result['url']);        $fileAbsoluteURL = $_SERVER['DOCUMENT_ROOT'] . $fileRelativePath;                if (file_exists($fileAbsoluteURL)) {            unlink($fileAbsoluteURL);        }    }        // Delete all records from the 'project_images' table where 'image_id' matches the 'id_image' from the element    $stmt2 = $db->prepare("DELETE FROM project_images WHERE image_id = ?");    $execute2 = $stmt2->execute([$element]);        // Delete the record from the 'images' table    $stmt3 = $db->prepare("DELETE FROM images WHERE id_image = ?");    $execute3 = $stmt3->execute([$element]);        if ($execute2 && $execute3) {        $stmt2->closeCursor();        $stmt3->closeCursor();        retour_erreur(2, 1);    } else {        retour_erreur(2, 2);    }?>