<?php	include("../_includes/session_test.inc");	include("../_includes/chabertdesign.inc");        if ($_SERVER['REQUEST_METHOD'] === 'POST') {        if (isset($_POST['imageOrder'])) {            // Retrieve the list of image IDs in the new order            $imageOrder = json_decode($_POST['imageOrder'], true);                        // Update the image placements in the database            $stmt = $db->prepare('UPDATE images SET img_placement = ? WHERE id_image = ?');                        foreach ($imageOrder as $position => $imageId) {                $stmt->execute([$position, $imageId]);            }                        // Close the prepared statement            $stmt->closeCursor();                        // Success message or redirection            retour_erreur(23, 231);        } else {            // Handle the case where $_POST['imageOrder'] is not set            retour_erreur(23, 232);        }    } else {        // Handle non-POST requests        retour_erreur(23, 233);    }    ?>