<?php    include("../_includes/session_test.inc");    include("../_includes/chabertdesign.inc");        if ($_SERVER['REQUEST_METHOD'] === 'POST') {        $project = $_POST['project']; // Retrieve the project ID from the POST data                if (isset($_POST['imageOrder'])) {            // Decode the JSON array of image IDs and their new order            $imageOrder = json_decode($_POST['imageOrder'], true);                        // Update the image placements in the database            $stmt = $db->prepare('UPDATE project_images SET image_place = ? WHERE image_id = ? AND project_id = ?');                        foreach ($imageOrder as $newPosition => $imageId) {                // Ensure the imageId and project are integers (for security)                $imageId = (int) $imageId;                $project = (int) $project;                                // Update the image placement                $stmt->execute([$newPosition, $imageId, $project]);            }                        // Close the prepared statement            $stmt->closeCursor();                        // Success message or redirection            retour_erreur_content(23, 231, 'project', $project); // You can customize this success message.        } else {            // Handle the case where $_POST['imageOrder'] is not set            retour_erreur_content(23, 232, 'project', $project); // Handle this error condition as needed.        }    } else {        // Handle non-POST requests        retour_erreur(23, 233); // Handle non-POST requests as needed.    }?>